import React from "react";

const Toggle = () => {
    return (
        <div
            class="toggle-container"
            onClick={() =>
                handleToggleDarkMode((previousDarkMode) => !previousDarkMode)
            }
        >
            <input type="checkbox" id="switch" name="theme" />
            <label for="switch">Toggle</label>
        </div>
    );
};

export default Toggle;

